import {Component} from "@angular/core";
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";

@Component({
  selector: "app-validators",
  templateUrl: "./validators.component.html",
  styleUrls: ["./validators.component.scss"]
})
export class ValidatorsComponent {

  myForm: FormGroup;

  constructor(fb: FormBuilder) {
    this.myForm = fb.group({
      "sku": ["", Validators.required],
      "surname": ["", Validators.compose([Validators.required, this.validateSurname])],
      "test": [""],
    });
  }

  onSubmit(value: string, event: Event): void {
    event.stopPropagation(); // prevent the default submit action of the browser. the parent component will be notified but the browser will not be redirected.
    console.log("you submitted value: ", value);
  }

  validateSurname(control: FormControl): { [p: string]: boolean } | null {
    if (!control.value.match(/^123/)) {
      return {invalidSurname: true};
    }
    return null;


  }

}
<div class="ui raised segment">
  <h2 class="ui header">Validators</h2>

  <form [formGroup]="myForm"

        (ngSubmit)="onSubmit(myForm.value,$event)"
        class="ui form">
    <div class="field">
      <label for="skuInput">SKU</label>
      <input formControlName="sku" id="skuInput" placeholder="SKU">
    </div>

    <div *ngIf="!myForm.controls['sku'].valid"
         class="ui error message">SKU is invalid
    </div>

    <div *ngIf="myForm.controls['sku'].hasError('invalidSku')"
         class="ui error message">SKU must begin with <span>123</span></div>

    <div class="field" [class.error]="!myForm.get('sku').valid && myForm.get('sku').touched">>

      <label for="surnameInp">Surname</label>
      <input formControlName="surname" id="surnameInp" placeholder="Rossi">

      <div *ngIf="!myForm.controls['surname'].valid"
           class="ui error message">SKU is invalid
      </div>

      <div *ngIf="myForm.controls['surname'].hasError('invalidSurname')"
           class="ui error message">SKU must begin with <span>123</span>
      </div>
    </div>


    <button class="ui button" type="submit">Submit</button>
  </form>
</div>
