import {Component} from "@angular/core";
import {FormBuilder, FormGroup} from "@angular/forms";

@Component({
  selector: "app-reactive-forms",
  templateUrl: "./reactive-forms.component.html",
  styleUrls: ["./reactive-forms.component.scss"]
})
export class ReactiveFormsComponent {
  myform: FormGroup | undefined;


  // At a high level, Dependency Injection is a way to tell Angular what
  // dependencies this component needs to function properly.

  constructor(fb: FormBuilder) {
    this.myform = fb.group({
      "sku": ["ABC123"]
    });
  }

  onSubmit(value: string): void {
    console.log("you submitted value: ", value);
  }

}
<h2>Reactive Forms</h2>
<div class="ui raised segment">
  <h2 class="ui header">Demo Form: SKU</h2>

  <!--  if you import FormsModule, NgForm will get automatically attached to any <form> tags you have in your view. It's a form control group-->
  <!--  ngForm is the -->
  <form #tempVar="ngForm"
        (ngSubmit)="onSubmit(tempVar.value)"
        class="ui form">
    <div class="field">
      <label for="skuInput">SKU</label>
      <!--      If you use ngModel without [(ngModel)]="propertyName" you create a form control. The name attribute is necessary for ngModel to register the control with the parent form-->
      <input ngModel name="sku" id="skuInput" placeholder="SKU">
    </div>

    <div class="field">
      <label for="surnameInp">Surname</label>
      <input ngModel name="surname" id="surnameInp" placeholder="Rossi">
    </div>

    <button class="ui button" type="submit">Submit</button>
  </form>
</div>
